function Register(){}Register.prototype.listenImgCaptchaEvent=function(){var t=$(".img-captcha-btn");t.click(function(){t.attr("src","/acauth/img_captcha/?random="+Math.random())})},Register.prototype.listenRegisterEvent=function(){var t=$("#register-btn"),s=$(".username-input"),o=$(".telephone-input"),p=$(".email-input"),u=$(".password1-input"),l=$(".password2-input"),g=$(".captcha-input");t.click(function(t){t.preventDefault();var a=s.val(),e=o.val(),n=p.val(),i=u.val(),c=l.val(),r=g.val();acajax.post({url:"/acauth/register/",data:{username:a,telephone:e,email:n,password1:i,password2:c,img_captcha:r},success:function(t){acalert.alertSuccess({msg:"注册成功！",confirmCallback:function(){window.location.reload()}})}})})},Register.prototype.run=function(){this.listenRegisterEvent(),this.listenImgCaptchaEvent()},$(function(){(new Register).run()});